package cn.poile.ucs.auth.config;import cn.poile.ucs.auth.provider.MobileCodeAuthenticationProvider;import org.springframework.data.redis.core.StringRedisTemplate;import cn.poile.ucs.auth.service.UserDetailsServiceImpl;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import org.springframework.security.authentication.AuthenticationManager;import org.springframework.security.config.annotation.authentication.builders.AuthenticationManagerBuilder;import org.springframework.security.config.annotation.web.builders.HttpSecurity;import org.springframework.security.config.annotation.web.configuration.EnableWebSecurity;import org.springframework.security.config.annotation.web.configuration.WebSecurityConfigurerAdapter;import org.springframework.security.crypto.bcrypt.BCryptPasswordEncoder;import org.springframework.security.crypto.password.PasswordEncoder;/** * security web安全配置,spring-cloud-starter-oauth2依赖于security * @author: yaohw * @create: 2019-09-25 16:49 */@Configuration@EnableWebSecuritypublic class SecurityConfigurerAdapter extends WebSecurityConfigurerAdapter {    @Autowired    private UserDetailsServiceImpl userDetailsService;    @Autowired    private StringRedisTemplate stringRedisTemplate;    /**     * 配置认证管理器     * @return     * @throws Exception     */    @Bean    @Override    public AuthenticationManager authenticationManagerBean() throws Exception {        return super.authenticationManagerBean();    }    /**     * 配置密码加密对象（解密时会用到PasswordEncoder的matches判断是否正确）     * 用户的password和客户端clientSecret用到，所以存的时候存该bean encode过的密码     * @return     */    @Bean    public PasswordEncoder passwordEncoder() {        return new BCryptPasswordEncoder();    }    /**     * 这里是对认证管理器的添加配置     * @param auth     * @throws Exception     */    @Override    protected void configure(AuthenticationManagerBuilder auth) throws Exception {        auth.authenticationProvider(provider())                .userDetailsService(userDetailsService)                .passwordEncoder(new BCryptPasswordEncoder());    }    @Override    protected void configure(HttpSecurity http) throws Exception {        http                .httpBasic().and()                .csrf().disable();    }    /**     * 自定义手机验证码认证提供者     * @return     */    @Bean    public MobileCodeAuthenticationProvider provider() {        MobileCodeAuthenticationProvider provider = new MobileCodeAuthenticationProvider();        provider.setStringRedisTemplate(stringRedisTemplate);        provider.setHideUserNotFoundExceptions(false);        provider.setUserDetailsService(userDetailsService);        return provider;    }}